import React, { Component, PropTypes } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios               from 'axios';
import 'font-awesome/css/font-awesome.min.css';
import 'bootstrap/js/modal.js';
import swal                       from 'sweetalert';
import $ from "jquery";
import Add_classRating              from './add_classRating.js';
/*import Add_dataTable           from './add_dataTable.js';*/

class classRating extends Component {


    constructor(props) {
        super(props);
          this.state = {
        allPosts : [],
        propclass : "",
        earning   : "",
    }

    this.handleChange = this.handleChange.bind(this);

    }

    componentDidMount(){

    axios
      .get('/api/mastersellometers/list')
      .then(
        (res)=>{
          console.log('res', res);
          const postsdata = res.data;
          console.log('postsdata',postsdata);
          this.setState({
            allPosts : postsdata,
          });
        }
      )
      .catch((error)=>{

        console.log("error = ",error);
        // alert("Something went wrong! Please check Get URL.");
         });        

  }

  selectedData(data){
    this.setState({
      allPosts : data,
    })

    console.log("here full data in props ______________________________",this.state.allPosts);
  }


      deleteData(event){
        event.preventDefault();
      var id = event.target.id;
      console.log("id",id);
      const token = '';
      const url = '/api/mastersellometers/'+id ;
      const headers = {
            "Authorization" : token,
            "Content-Type"  : "application/json",
