          method: "DELETE",
          url : url,
          headers: headers,
          timeout: 3000,
          data: null,
        })
        .then((response)=> {
            console.log('delete response',response);
            swal("Property class deleted successfully","", "success");


             axios
            .get('/api/mastersellometers/list')
            .then(
              (res)=>{
                console.log('res', res);
                const postsdata = res.data;
                console.log('postsdata',postsdata);
                this.setState({
                  allPosts : postsdata,
                });
              }
            )
            .catch((error)=>{

              console.log("error = ",error);
              // alert("Something went wrong! Please check Get URL.");
               });       


        }).catch((error)=> {
            // handle error
            console.log(error);
        });
      }

      ClearData(event){
        this.setState({
          propclass : "",
          earning : "",
        });

      }

      editData(event){

        event.preventDefault();
      var id = event.target.id;
      console.log("edit id",id);

      const formValues = {
          "class"     : this.state.propclass,
          "earnings"   : this.state.earning,
          }
          console.log("formValues",formValues);
      if(this.state.propclass!=""&& this.state.earning!="")
      {
        axios.put('/api/mastersellometers/'+id, formValues)
          .then( (res)=>{
              console.log("submit ");
              swal("Property class Updated successfully", "", "success");

              this.setState({
                propclass : "",
                earning   : "",

              });


               axios
                .get('/api/mastersellometers/list')
                .then(
                  (res)=>{
                    console.log('res', res);
                    const postsdata = res.data;
                    console.log('postsdata',postsdata);
                    this.setState({
