import React, { Component } from 'react';
import { render }           from 'react-dom';
import $ from "jquery";
import axios from 'axios';
// import SimpleReactValidator from 'simple-react-validator';
import swal from 'sweetalert';
import InputMask  from 'react-input-mask';
import S3FileUpload from 'react-s3';
import "../../../API";

const formValid = formerrors=>{
  console.log("formerrors",formerrors);
  let valid = true;
  Object.values(formerrors).forEach(val=>{
  val.length>0 && (valid = false);
  })
  return valid;
  }

const companymobileRegex  = RegExp(/^[0-9][0-9]{9}$|^$/);
const companyAddressRegex = RegExp(/^[a-zA-Z0-9\s,'/.-]*$/);
const companywebsiteRegex = RegExp(/^((https?|ftp|smtp):\/\/)?(www.)?[a-z0-9]+\.[a-z]+(\/[a-zA-Z0-9#]+\/?)*$/);
const companypincodeRegex = RegExp(/^[1-9][0-9]{5}$/);
const companynameRegex = RegExp(/^[A-za-z']+( [A-Za-z']+)*$/);
const emailRegex = RegExp (/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$|^$/);
class CompanyInformation extends Component{
   constructor(props) {
    super(props);
    this.state = {
      config                  : "",
      userProfile             : "",
      companyId               : "",
      companyName             : "",
      companyContactNumber    : "",
      companyAltContactNumber : "",
      companyEmail            : "",
      companyAddressLine1     : "",
      companyDist             : "",
      companyPincode          : "",
      companyCity             : "",
      companyState            : "",
      companyCountry          : "",
      companyLogo             : "",
      logoFilename            : "",
      taluka                  : "",
      companywebsite          : "",
      data                    : [],
      submitVal               : true,
      formerrors :{
        firstcompanyname : "",
        companyMobile : " ",
        companyEmailID  : " ",
        companyAddress : " ",
        companywebsitename : " ",

        country : " ",
        district : " ",
        state : " ",
        taluka : " ",

        city : " ",
        pincode : " ",
      },
      subscription : {
      }
    };
    this.handleChange = this.handleChange.bind(this);
  }
  componentDidMount() {
  var companyId = 1;
    axios.get('/api/tgkSpecificcompanysettings/'+ companyId)
    .then( (res)=>{      
      console.log("here company data",res.data);
      this.setState({
        companyName : res.data.companyName,
        companyId   : 1,
        companyContactNumber : res.data.companyContactNumber,
        companyAltContactNumber : res.data.companyMobileNumber,
        companyEmail            : res.data.companyEmail, 
        companywebsite          : res.data.companywebsite, 
        companyAddressLine1     : res.data.companyaddress,
        companyDist             : res.data.district,
        companyPincode          : res.data.pincode,
        companyCity             : res.data.city,
        companyState            : res.data.state,
        companyCountry          : res.data.country,
        taluka                  : res.data.taluka,
        submitVal               : false,
      });
      console.log("this.this.state.companyName",this.state.companyName)
    })
    .catch((error)=>{
      console.log("error = ",error);
      // alert("Something went wrong! Please check Get URL.");
    });

  }

  removeCompanyImage(event){
    event.preventDefault();
    // var link = $(event.target).attr('data-link');
  }

  CompanyImage(){
    // var TempIamge = this.props.tempLogoData;
    // if(TempIamge){
    //   // this.setState({
    //   //   commonLogo : TempIamge.logo
    //   // })
    //   return TempIamge.logo;
    // }else{
    //   var companyData = CompanySettings.findOne({});
    //   if(companyData){
    //     if(companyData.companyLogo){
    //       // this.setState({
    //       //   commonLogo : companyData.companyLogo
    //       // })
    //       return companyData.companyLogo;
    //     }else{
    //       return '/images/CSLogo.png';
    //     }
    //   } else{
    //     return '/images/CSLogo.png';
    //   }
    // }
  }
  imgBrowse(event){
   event.preventDefault();
     // let self = this;
     // if (event.currentTarget.files && event.currentTarget.files[0]) {
     // var file = event.currentTarget.files[0];
     //    if (file) {
     //      var fileName  = file.name; 
     //    console.log("fileName--------------->",fileName);
     //        var ext       = fileName.split('.').pop();  
     //                if(ext=="jpg" || ext=="png" || ext=="jpeg" || ext=="JPG" || ext=="PNG" || ext=="JPEG"){    
     //                     if (file) {
     //                      S3FileUpload
     // .uploadFile(file,this.state.config)
     // .then((Data)=>{
     //  console.log("Data = ",Data);
     //  this.setState({
     //  userProfile : Data.location
     //  },()=>{
     //       console.log("state of userprofile", this.state.userProfile); 
     //  })


            axios
             .get('/api/projectsettings/get/one/S3')
             .then((response)=>{
              console.log("response data hereeeeeeeeeeeeeeeeeee",response);
              const config = {
                 bucketName : response.data.bucket,
                 dirName    : 'photos',
                 region : response.data.region,
                 accessKeyId : response.data.key,
                 secretAccessKey : response.data.secret,
              }
              this.setState({
              config : config
              })
                     })
                     .catch(function(error){
                        console.log(error);
                     })

     //  })
     //  .catch((error)=>{
     //     console.log(error);
     //  })
     //      // addStudentProfileImage(file,self);
     //  }else{          
     //          swal("File not uploaded","Something went wrong","error");  
     //             }     
     //                }else{ 
     //                    swal("Please upload file","Only Upload  images format (jpg,png,jpeg)","warning");   
     //                 }
     //  }

     // }

  }

  componentWillReceiveProps(nextProps) {

    if(nextProps.get && nextProps.get.length != 0){

     this.setState({
                companyName             : this.state.companyName,
                companyContactNumber    : this.state.companyContactNumber,
                companyAltContactNumber : this.state.companyAltContactNumber,
                companyEmail            : this.state.companyEmail,
                companywebsite          : this.state.companywebsite,
                companyaddress          : this.state.companyAddressLine1,
                country                 : this.state.companyCountry,
                state                   : this.state.companyState,
                district                : this.state.companyDist,
                city                    : this.state.companyCity,
                pincode                 : this.state.companyPincode,
                taluka                  : this.state.taluka,
                logoFilename            : this.state.logoFilename,
                companyLogo             : this.state.companyLogo,

     });
    }

    this.handleChange = this.handleChange.bind(this);
  }

  submitCompanyInformation=(event)=>{
    event.preventDefault();

    var companyInfoFormValue = {

      companyName             : this.state.companyName,
      companyContactNumber    : this.state.companyContactNumber,
      companyMobileNumber     : this.state.companyAltContactNumber,
      companyEmail            : this.state.companyEmail,
      companyAltEmail         : "",
      companywebsite          : this.state.companywebsite,
      companyaddress          : this.state.companyAddressLine1,
      logoFilename            : this.state.logoFilename,
      companyLogo             : this.state.companyLogo,
      country                 : this.state.companyCountry,
      state                   : this.state.companyState,
      district                : this.state.companyDist,
      city                    : this.state.companyCity,
      pincode                 : this.state.companyPincode,
      taluka                  : this.state.taluka,
    }//close array

     var companyInfoFormValueUpdate = {

      companyId               :1,
      companyName             : this.state.companyName,
      companyContactNumber    : this.state.companyContactNumber,
      companyMobileNumber     : this.state.companyAltContactNumber,
      companyEmail            : this.state.companyEmail,
      companyAltEmail         : "",
      companywebsite          : this.state.companywebsite,
      companyaddress          : this.state.companyAddressLine1,
      logoFilename            : this.state.logoFilename,
      companyLogo             : this.state.companyLogo,
      country                 : this.state.companyCountry,
      state                   : this.state.companyState,
      district                : this.state.companyDist,
      city                    : this.state.companyCity,
      pincode                 : this.state.companyPincode,
      taluka                  : this.state.taluka,
    }

    console.log("companyInfoFormValueUpdate--------------------",companyInfoFormValueUpdate);
    // formValid(this.state.formerrors)
  if(this.state.companyName!= "" && this.state.companyContactNumber!= "" && this.state.companyEmail!= ""
   && this.state.companywebsite!= "" && this.state.companyAddressLine1!= "" && this.state.companyCountry!= ""
   && this.state.companyState!= "" && this.state.companyDist!= "" && this.state.companyCity!= "" && 
    this.state.companyPincode!= "" && this.state.taluka!= ""){


    if(this.state.submitVal == true){
        axios.post('/api/tgkSpecificcompanysettings',companyInfoFormValue)
        .then( (response)=> {
          // handle success
          console.log("this is response===>>>",response);
          swal("Good job!", "Company Information submitted Successfully", "success");
          this.setState({

          companyName             : "",
          companyContactNumber    : "",
          companyAltContactNumber : "",
          companyEmail            : "",
          companyAddressLine1     : "",
          companyDist             : "",
          companyPincode          : "",
          companyCity             : "",
          companyState            : "",
          companyCountry          : "",
          companyLogo             : "",
          logoFilename            : "",
          taluka                  : "",
          companywebsite          : "",
          });



        })
        .catch(function (error) {
          // handle error
          console.log(error);
          swal("Sorry!", "Company Information submition failed!", "error")

        })
        .finally(function () {
          // always executed
        });
    }else{
      // upate function

      console.log("update axios");
      axios.patch('/api/tgkSpecificcompanysettings/information',companyInfoFormValueUpdate)
        .then( (response)=> {
          // handle success
          console.log("this is response===>>>",response);
          swal("Good job!", "Company Information Updated Successfully", "success");

           // after update show updated data
                        var companyId = 1;
                        axios.get('/api/tgkSpecificcompanysettings/'+ companyId)
                        .then( (res)=>{      
                          // console.log("here company data",res.data);
                          this.setState({
                            companyName : res.data.companyName,
                            companyId   : 1,
                            companyContactNumber : res.data.companyContactNumber,
                            companyAltContactNumber : res.data.companyMobileNumber,
                            companyEmail            : res.data.companyEmail, 
                            companywebsite          : res.data.companywebsite, 
                            companyAddressLine1     : res.data.companyaddress,
                            companyDist             : res.data.district,
                            companyPincode          : res.data.pincode,
                            companyCity             : res.data.city,
                            companyState            : res.data.state,
                            companyCountry          : res.data.country,
                            taluka                  : res.data.taluka,
                            submitVal               : false,
                          });

                        })
                        .catch((error)=>{
                          console.log("error = ",error);
                          // alert("Something went wrong! Please check Get URL.");
                        });


        })
        .catch(function (error) {
          // handle error
          console.log(error);
          swal("", "Company Information updation failed!", "Danger")

        });
    }
  }else{
    swal("Please enter mandatory fields", "", "warning");
    console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
  }






  }
  handleChange(event){
    // const target = event.target;
    // const {name , value}   = event.target;
    const datatype = event.target.getAttribute('data-text');
    const {name,value} = event.target;
    let formerrors = this.state.formerrors;

    console.log("datatype",datatype);
    switch (datatype){

      case 'firstcompanyname' : 
       formerrors.firstcompanyname = companynameRegex.test(value)  && value.length>0 ? '' : "Please Enter Valid Name";
       break;

       case 'companyMobile' : 
       formerrors.companyMobile = companymobileRegex.test(value) && value.length>0 ? '' : "Please enter a valid Contact Number";
       break;

       case 'companyEmailID' : 
        formerrors.companyEmailID = emailRegex.test(value)  && value.length>0? "":"Please enter a valid Email ID";
       break;
      //  case "email" : 
      //  formErrors.email = emailRegex.test(value)? "":"Please enter valid mail address";
      //  break;
      case 'companywebsitename' : 
         formerrors.companywebsitename = companywebsiteRegex.test(value)  && value.length>0 ? '' : "Please enter a valid Company Website";
      break;

      case 'companyAddress' : 
       formerrors.companyAddress = companyAddressRegex.test(value)  && value.length>0 ? '' : "Please enter a valid Address";
      break;

      case 'country' : 
        formerrors.country = companynameRegex.test(value)  && value.length>0 ? '' : "Please enter a valid Country";
      break;

      case 'state' : 
        formerrors.state = companynameRegex.test(value)  && value.length>0 ? '' : "Please enter a valid State";
      break;

      case 'district' : 
        formerrors.district = companynameRegex.test(value)  && value.length>0 ? '' : "Please enter a valid District";
      break;

      case 'taluka' : 
        formerrors.taluka = companynameRegex.test(value)  && value.length>0 ? '' : "Please enter a valid Taluka";
      break;

      case 'city' : 
        formerrors.city = companynameRegex.test(value)  && value.length>0 ? '' : "Please enter a valid City";
      break;

      case 'pincode' : 
        formerrors.pincode = companypincodeRegex.test(value)   && value.length>0? '' : "Please enter a valid Pincode";
      break;

       default :
       break;

      //  case 'companyName' : 
      //  formerrors.companyName = value.length < 1 && value.lenght > 0 ? 'Minimum 1 Character required' : "";
      //  break;

    }
    // this.setState({formerrors,})
    this.setState({ formerrors,
      [name]:value
    } );
  }

